// Debug first to see what we're receiving
const input = $input.first();
console.log('Input keys:', Object.keys(input));
console.log('Has binary?', !!input.binary);
console.log('Binary keys:', input.binary ? Object.keys(input.binary) : 'No binary');

// Get story audio from TTS - check different possible locations
let storyBase64;

// Try different ways to access the binary data
if (input.binary && input.binary.data) {
  storyBase64 = input.binary.data.data;
  console.log('Found story audio in binary.data.data');
} else if (input.binary && input.binary.audio) {
  storyBase64 = input.binary.audio.data;
  console.log('Found story audio in binary.audio.data');
} else if (input.json && input.json.audioContent) {
  // Google TTS might return base64 in JSON
  storyBase64 = input.json.audioContent;
  console.log('Found story audio in json.audioContent');
} else {
  // Log what we have for debugging
  console.log('Binary structure:', JSON.stringify(input.binary, null, 2));
  console.log('JSON structure:', JSON.stringify(input.json, null, 2));
  throw new Error('Could not find story audio in expected location');
}

// Get intro/outro from Edit Fields
const introBase64 = $('Edit Fields').item.json.intro_base64;
const outroBase64 = $('Edit Fields').item.json.outro_base64;

// Get child details from webhook
const webhookData = $('Webhook').item.json;
// Handle both body and direct access
const childName = webhookData.body?.childName || webhookData.childName;
const age = webhookData.body?.age || webhookData.age;
const theme = webhookData.body?.theme || webhookData.theme;
const parentEmail = webhookData.body?.parentEmail || webhookData.parentEmail;

console.log('Data collected:', {
  hasIntro: !!introBase64,
  hasStory: !!storyBase64,
  hasOutro: !!outroBase64,
  childName,
  age,
  theme,
  parentEmail
});

// Pass everything forward
return [{
  json: {
    // Audio data
    introBase64,
    storyBase64,
    outroBase64,

    // Child details
    childName,
    age,
    theme,
    parentEmail,

    // Metadata
    timestamp: new Date().toISOString(),
    storyLength: storyBase64 ? storyBase64.length : 0
  }
}];

// Get all data from the previous node (Prepare Audio Data)
const data = $input.first().json;

// Extract all the data we need
const introBase64 = data.introBase64;
const storyBase64 = data.storyBase64;
const outroBase64 = data.outroBase64;

const childName = data.childName;
const age = data.age;
const theme = data.theme;
const parentEmail = data.parentEmail;

// Validate we have all required data
if (!introBase64 || !storyBase64 || !outroBase64) {
  throw new Error(`Missing audio data: intro=${!!introBase64}, story=${!!storyBase64}, outro=${!!outroBase64}`);
}

// Get current date in Hebrew
const date = new Date().toLocaleDateString('he-IL', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Create the full interactive HTML content with embedded audio player
// NOTE: This creates a complete HTML file with embedded base64 audio
// The HTML includes: Hebrew RTL layout, interactive audio player, 
// progress tracking, and WhatsApp sharing functionality
const htmlContent = `<!-- COMPLETE HTML PLAYER CODE HERE -->
<!-- Includes: Hebrew storytelling interface, embedded audio player, -->
<!-- progress tracking, and social sharing features -->`;

// Create filename
const fileName = `story-${childName}-${Date.now()}.html`;

console.log('HTML generated successfully');
console.log(`File size: ${Math.round(htmlContent.length / 1024)} KB`);

// Return BOTH JSON and binary data for the hybrid approach
return [{
  json: {
    fileName: fileName,
    parentEmail: parentEmail,
    childName: childName,
    theme: theme,
    age: age,
    fileSize: Math.round(htmlContent.length / 1024) + ' KB',
    timestamp: new Date().toISOString()
  },
  binary: {
    data: {
      data: Buffer.from(htmlContent).toString('base64'),
      mimeType: 'text/html',
      fileName: fileName
    }
  }
}];

// Parse the body string into an object
const parsedBody = JSON.parse($input.first().json.body);

// Return a clean data structure
return {
  child_name: parsedBody.child_name,
  age: parsedBody.age,
  story_theme: parsedBody.story_theme,
  parent_email: parsedBody.parent_email,
  // Keep original data for reference if needed
  _original: $input.first().json
};
